-- Insert AEPP questions
INSERT INTO public.questions (
  category_id, 
  question_text, 
  question_type, 
  correct_answer, 
  difficulty_level, 
  points_value,
  explanation
) 
SELECT 
  c.id,
  v.question_text,
  'fill-in-the-blank',
  v.correct_answer,
  v.difficulty_level,
  v.points_value,
  v.explanation
FROM public.categories c
CROSS JOIN (
  VALUES 
    ('Προβλήματα και Δεδομένα', 'Με τον όρο Πρόβλημα εννοείται μια ________ η οποία ________ ________, απαιτεί ________, η δε ________ της δεν είναι ________, ούτε ________.', 'κατάσταση|χρήζει|αντιμετώπισης|λύση|λύση|γνωστή|προφανής', 1, 10, 'Με τον όρο Πρόβλημα εννοείται μια κατάσταση η οποία χρήζει αντιμετώπισης, απαιτεί λύση, η δε λύση της δεν είναι γνωστή, ούτε προφανής. (ΑΕΠΠ σ.15)'),
    ('Προβλήματα και Δεδομένα', 'Με τον όρο δεδομένο δηλώνεται οποιοδήποτε ________ μπορεί να γίνει ________ από έναν ________ ________ με μία από τις ________ ________ του.', 'στοιχείο|αντιληπτό|τουλάχιστον|παρατηρητή|πέντε|αισθήσεις', 1, 10, 'Με τον όρο δεδομένο δηλώνεται οποιοδήποτε στοιχείο μπορεί να γίνει αντιληπτό από έναν τουλάχιστον παρατηρητή με μία από τις πέντε αισθήσεις του. (ΑΕΠΠ σ. 19)'),
    ('Προβλήματα και Δεδομένα', 'Με τον όρο πληροφορία αναφέρεται οποιοδήποτε ________ ________ προέρχεται από ________ ________.', 'γνωσιακό|στοιχείο|επεξεργασία|δεδομένων', 1, 10, 'Με τον όρο πληροφορία αναφέρεται οποιοδήποτε γνωσιακό στοιχείο προέρχεται από επεξεργασία δεδομένων. (ΑΕΠΠ σ. 19)'),
    ('Προβλήματα και Δεδομένα', 'Με τον όρο δομή ενός προβλήματος αναφερόμαστε στα ________ του ________, στα ________ ________ που το αποτελούν καθώς επίσης και στον ________ που αυτά τα ________ ________ μεταξύ τους.', 'συστατικά|μέρη|επιμέρους|τμήματα|τρόπο|μέρη|συνδέονται', 2, 15, 'Με τον όρο δομή ενός προβλήματος αναφερόμαστε στα συστατικά του μέρη, στα επιμέρους τμήματα που το αποτελούν καθώς επίσης και στον τρόπο που αυτά τα μέρη συνδέονται μεταξύ τους. (ΑΕΠΠ σ. 19)'),
    ('Αλγόριθμοι', 'Αλγόριθμος είναι μια ________ ________ ________, ________ ________ και ________ σε ________ χρόνο, που στοχεύουν στην ________ ενός ________.', 'πεπερασμένη|σειρά|ενεργειών|αυστηρά|καθορισμένων|εκτελέσιμων|πεπερασμένο|επίλυση|προβλήματος', 2, 15, 'Αλγόριθμος είναι μια πεπερασμένη σειρά ενεργειών, αυστηρά καθορισμένων και εκτελέσιμων σε πεπερασμένο χρόνο, που στοχεύουν στην επίλυση ενός προβλήματος.(ΑΕΠΠ σ 33 .)'),
    ('Αλγόριθμοι', 'Τα στάδια αντιμετώπισης ενός προβλήματος είναι τρία: 1. ________ όπου απαιτείται η σωστή και πλήρης αποσαφήνιση των δεδομένων και των ζητούμενων του προβλήματος 2. ________ όπου το αρχικό πρόβλημα διασπάται σε άλλα επιμέρους απλούστερα προβλήματα 3. ________ όπου υλοποιείται η λύση του προβλήματος.', 'Κατανόηση|Ανάλυση|Επίλυση', 2, 15, 'Τα στάδια αντιμετώπισης ενός προβλήματος είναι τρία: 1. Κατανόηση, όπου απαιτείται η σωστή και πλήρης αποσαφήνιση των δεδομένων και των ζητούμενων του προβλήματος 2. Ανάλυση, όπου το αρχικό πρόβλημα διασπάται σε άλλα επιμέρους απλούστερα προβλήματα 3. Επίλυση, όπου υλοποιείται η λύση του προβλήματος, μέσω της λύσης των επιμέρους προβλημάτων.(ΑΕΠΠ σ. 25)'),
    ('Αλγόριθμοι', 'Τρόποι αναπαράστασης αλγορίθμου: ________ κείμενο, ________ τεχνικές, ________ γλώσσα (κατά βήματα), ________.', 'Ελεύθερο|Διαγραμματικές|Φυσική|Κωδικοποίηση', 2, 15, 'Τρόποι αναπαράστασης αλγορίθμου: Ελεύθερο κείμενο, Διαγραμματικές τεχνικές, Φυσική γλώσσα (κατά βήματα), Κωδικοποίηση'),
    ('Αλγόριθμοι', 'Κριτήρια Αλγορίθμου: ________, ΕΞΟΔΟΣ, ________ ΗΤΑ, ________ ΗΤΑ, ________ ΗΤΑ', 'ΕΙΣΟΔΟΣ|ΚΑΘΟΡΙΣΤΙΚΟΤ|ΠΕΡΑΤΟΤ|ΑΠΟΤΕΛΕΣΜΑΤΙΚΟΤ', 3, 20, 'Κριτήρια Αλγορίθμου: ΕΙΣΟΔΟΣ, ΕΞΟΔΟΣ, ΚΑΘΟΡΙΣΤΙΚΟΤΗΤΑ, ΠΕΡΑΤΟΤΗΤΑ, ΑΠΟΤΕΛΕΣΜΑΤΙΚΟΤΗΤΑ'),
    ('Δομές Δεδομένων', 'Δομή Δεδομένων είναι ένα ________ ________ ________ που υφίστανται ________ από ένα ________ ________.', 'σύνολο|αποθηκευμένων|δεδομένων|επεξεργασία|σύνολο|λειτουργιών', 2, 15, 'Δομή Δεδομένων είναι ένα σύνολο αποθηκευμένων δεδομένων που υφίστανται επεξεργασία από ένα σύνολο λειτουργιών.(ΑΕΠΠ σ. 56)'),
    ('Δομές Δεδομένων', 'Βασικές Λειτουργίες Δομών Δεδομένων: ________ είναι η πρόσβαση σε έναν κόμβο, ________ είναι η προσθήκη νέων κόμβων, ________ αποτελεί το αντίστροφο της εισαγωγής, ________ είναι λειτουργία προσπέλασης κόμβων.', 'ΠΡΟΣΠΕΛΑΣΗ|ΕΙΣΑΓΩΓΗ|ΔΙΑΓΡΑΦΗ|ΑΝΑΖΗΤΗΣΗ', 3, 20, 'Βασικές Λειτουργίες: ΠΡΟΣΠΕΛΑΣΗ, ΕΙΣΑΓΩΓΗ, ΔΙΑΓΡΑΦΗ, ΑΝΑΖΗΤΗΣΗ, ΤΑΞΙΝΟΜΗΣΗ, ΑΝΤΙΓΡΑΦΗ, ΣΥΓΧΩΝΕΥΣΗ, ΔΙΑΧΩΡΙΣΜΟΣ'),
    ('Ταξινόμηση και Αναζήτηση', 'Η σειριακή μέθοδος αναζήτησης δικαιολογείται όταν: 1. Ο πίνακας είναι ________ ________ 2. Ο πίνακας είναι ________ ________ 3. Η αναζήτηση ________ ________', 'μη|ταξινομημένος|μικρού|μεγέθους|σπάνια|γίνεται', 2, 15, 'Η σειριακή μέθοδος αναζήτησης δικαιολογείται όταν: 1. Ο πίνακας είναι μη ταξινομημένος 2. Ο πίνακας είναι μικρού μεγέθους 3. Η αναζήτηση γίνεται σπάνια'),
    ('Ταξινόμηση και Αναζήτηση', 'Η ταξινόμηση συνίσταται στη ________ της ________ των ________, ώστε να τοποθετηθούν σε μία σειρά με συγκεκριμένη διάταξη.', 'μετάθεση|θέσης|στοιχείων', 2, 15, 'Η ταξινόμηση συνίσταται στη μετάθεση της θέσης των στοιχείων, ώστε να τοποθετηθούν σε μία σειρά με συγκεκριμένη διάταξη'),
    ('Γλώσσες Προγραμματισμού', 'Μία γλώσσα προσδιορίζεται από το ________ της, το ________ της, τη ________ της και τέλος τη σημασιολογία της.', 'αλφάβητό|λεξιλόγιό|γραμματική', 2, 15, 'Μία γλώσσα προσδιορίζεται από το αλφάβητό της, το λεξιλόγιό της, τη γραμματική της και τέλος τη σημασιολογία της.(ΑΕΠΠ σ. 115)')
) AS v(category_name, question_text, correct_answer, difficulty_level, points_value, explanation)
WHERE c.name = v.category_name;