-- Create categories for each chapter
INSERT INTO public.categories (name, description, difficulty_level, order_index) VALUES
('Κεφάλαιο 1: Προβλήματα και Δεδομένα', 'Βασικές έννοιες προβλημάτων, δεδομένων και πληροφοριών', 1, 1),
('Κεφάλαιο 2: Αλγόριθμοι και Δομές', 'Αλγόριθμοι, δομές δεδομένων και βασικές λειτουργίες', 2, 2),
('Κεφάλαιο 4: Μέθοδοι Επίλυσης', 'Μέθοδοι ανάλυσης και επίλυσης προβλημάτων', 3, 3),
('Κεφάλαιο 6: Γλώσσες Προγραμματισμού', 'Χαρακτηριστικά γλωσσών και δομημένος προγραμματισμός', 4, 4),
('Κεφάλαιο 8: Προχωρημένα Θέματα', 'Βρόχοι, μεταγλωττιστές, πίνακες και υποπρογράμματα', 5, 5);

-- Get category IDs for inserting exercises
DO $$
DECLARE
    cat1_id uuid;
    cat2_id uuid;
    cat4_id uuid;
    cat6_id uuid;
    cat8_id uuid;
BEGIN
    SELECT id INTO cat1_id FROM public.categories WHERE name = 'Κεφάλαιο 1: Προβλήματα και Δεδομένα';
    SELECT id INTO cat2_id FROM public.categories WHERE name = 'Κεφάλαιο 2: Αλγόριθμοι και Δομές';
    SELECT id INTO cat4_id FROM public.categories WHERE name = 'Κεφάλαιο 4: Μέθοδοι Επίλυσης';
    SELECT id INTO cat6_id FROM public.categories WHERE name = 'Κεφάλαιο 6: Γλώσσες Προγραμματισμού';
    SELECT id INTO cat8_id FROM public.categories WHERE name = 'Κεφάλαιο 8: Προχωρημένα Θέματα';

    -- Κεφάλαιο 1 exercises
    INSERT INTO public.fill_blank_exercises (category_id, exercise_text, answers, difficulty_level) VALUES
    (cat1_id, 'Με τον όρο Πρόβλημα εννοείται μια ________ η οποία ________ ________, απαιτεί ________, η δε ________ της δεν είναι ________, ούτε ________.', ARRAY['κατάσταση', 'χρήζει', 'αντιμετώπισης', 'λύση', 'λύση', 'γνωστή', 'προφανής'], 1),
    
    (cat1_id, 'Με τον όρο δεδομένο δηλώνεται οποιοδήποτε ________ μπορεί να γίνει ________ από έναν ________ ________ με μία από τις ________ ________ του.', ARRAY['στοιχείο', 'αντιληπτό', 'τουλάχιστον', 'παρατηρητή', 'πέντε', 'αισθήσεις'], 1),
    
    (cat1_id, 'Με τον όρο πληροφορία αναφέρεται οποιοδήποτε ________ ________ προέρχεται από ________ ________.', ARRAY['γνωσιακό', 'στοιχείο', 'επεξεργασία', 'δεδομένων'], 1),
    
    (cat1_id, 'Ο όρος επεξεργασία δεδομένων δηλώνει εκείνη τη ________ κατά την οποία ένας ________ δέχεται ________, τα επεξεργάζεται σύμφωνα με έναν ________ ________ και αποδίδει ________.', ARRAY['διαδικασία', 'μηχανισμός', 'δεδομένα', 'προκαθορισμένο', 'τρόπο', 'πληροφορίες'], 1),
    
    (cat1_id, 'Με τον όρο δομή ενός προβλήματος αναφερόμαστε στα ________ του ________, στα ________ ________ που το αποτελούν καθώς επίσης και στον ________ που αυτά τα ________ ________ μεταξύ τους.', ARRAY['συστατικά', 'μέρη', 'επιμέρους', 'τμήματα', 'τρόπο', 'μέρη', 'συνδέονται'], 1),
    
    (cat1_id, 'Τα στάδια αντιμετώπισης ενός προβλήματος είναι τρία: 1. ________ όπου απαιτείται η σωστή και πλήρης αποσαφήνιση των δεδομένων και των ζητούμενων του προβλήματος 2. ________ όπου το αρχικό πρόβλημα διασπάται σε άλλα επιμέρους απλούστερα προβλήματα 3. ________ όπου υλοποιείται η λύση του προβλήματος, μέσω της λύσης των επιμέρους προβλημάτων.', ARRAY['Κατανόηση', 'Ανάλυση', 'Επίλυση'], 1);

    -- Κεφάλαιο 2 exercises
    INSERT INTO public.fill_blank_exercises (category_id, exercise_text, answers, difficulty_level) VALUES
    (cat2_id, 'Αλγόριθμος είναι μια ________ ________ ________, ________ ________ και ________ σε ________ χρόνο, που στοχεύουν στην ________ ενός ________.', ARRAY['πεπερασμένη', 'σειρά', 'ενεργειών', 'αυστηρά', 'καθορισμένων', 'εκτελέσιμων', 'πεπερασμένο', 'επίλυση', 'προβλήματος'], 2),
    
    (cat2_id, 'Τρόποι αναπαράστασης αλγορίθμου: ________, ________ ________, ________ ________ (κατά βήματα), ________.', ARRAY['Ελεύθερο κείμενο', 'Διαγραμματικές', 'τεχνικές', 'Φυσική', 'γλώσσα', 'Κωδικοποίηση'], 2),
    
    (cat2_id, 'Κριτήρια Αλγορίθμου: ________, ΕΞΟΔΟΣ, ________, ________, ________.', ARRAY['ΕΙΣΟΔΟΣ', 'ΚΑΘΟΡΙΣΤΙΚΟΤΗΤΑ', 'ΠΕΡΑΤΟΤΗΤΑ', 'ΑΠΟΤΕΛΕΣΜΑΤΙΚΟΤΗΤΑ'], 2),
    
    (cat2_id, 'Δομή Δεδομένων είναι ένα ________ ________ ________ που υφίστανται ________ από ένα ________ ________.', ARRAY['σύνολο', 'αποθηκευμένων', 'δεδομένων', 'επεξεργασία', 'σύνολο', 'λειτουργιών'], 2),
    
    (cat2_id, 'Βασικές Λειτουργίες Δομών Δεδομένων: ________ είναι η πρόσβαση σε έναν κόμβο με σκοπό να εξετασθεί ή να τροποποιηθεί το περιεχόμενο του. ________ είναι η προσθήκη νέων κόμβων σε μία υπάρχουσα δομή.', ARRAY['ΠΡΟΣΠΕΛΑΣΗ', 'ΕΙΣΑΓΩΓΗ'], 2),
    
    (cat2_id, 'Η σειριακή μέθοδος αναζήτησης δικαιολογείται μόνο όταν: 1. Ο πίνακας είναι ________ ________ 2. Ο πίνακας είναι ________ ________ 3. Η αναζήτηση ________ ________.', ARRAY['μη', 'ταξινομημένος', 'μικρού', 'μεγέθους', 'σπάνια'], 2);

    -- Κεφάλαιο 4 exercises  
    INSERT INTO public.fill_blank_exercises (category_id, exercise_text, answers, difficulty_level) VALUES
    (cat4_id, 'Οι μέθοδοι ανάλυσης και επίλυσης των προβλημάτων παρουσιάζουν ενδιαφέρον γιατί: 1. παρέχουν ένα ________ ________ κατάλληλο για την ________ προβλημάτων ________ ________ 2. μπορούν να αναπαρασταθούν με ________ ________ ________ και ελέγχου που ________ από τις περισσότερες σύγχρονες ________ ________.', ARRAY['γενικό', 'πρότυπο', 'επίλυση', 'ευρείας', 'κλίμακας', 'κοινές', 'δομές', 'δεδομένων', 'υποστηρίζονται', 'γλώσσες', 'προγραμματισμού'], 3),
    
    (cat4_id, 'Οι μέθοδοι παρέχουν τη δυνατότητα καταγραφής των ________ και "________" απαιτήσεων της ________ επίλυσης, έτσι ώστε να μπορεί να γίνει ________ ________ των ________.', ARRAY['χρονικών', 'χωρικών', 'μεθόδου', 'επακριβής', 'εκτίμηση', 'αποτελεσμάτων'], 3);

    -- Κεφάλαιο 6 exercises
    INSERT INTO public.fill_blank_exercises (category_id, exercise_text, answers, difficulty_level) VALUES
    (cat6_id, 'Μία γλώσσα προσδιορίζεται από το ________ της, το ________ της, τη ________ της και τέλος τη σημασιολογία της.', ARRAY['αλφάβητό', 'λεξιλόγιό', 'γραμματική'], 4),
    
    (cat6_id, 'Πλεονεκτήματα του δομημένου προγραμματισμού: 1)Δημιουργία ________ προγραμμάτων. 2) ________ μεταφορά των ________ σε ________. 3)Διευκόλυνση ________ του προγράμματος σε ________.', ARRAY['απλούστερων', 'Άμεση', 'αλγορίθμων', 'προγράμματα', 'ανάλυσης', 'τμήματα'], 4),
    
    (cat6_id, '4) ________ των ________ κατά την ανάπτυξη του προγράμματος. 5)Διευκόλυνση στην ________ και ________ του προγράμματος από τρίτους. 6)Ευκολότερη ________ και ________.', ARRAY['Περιορισμός', 'λαθών', 'ανάγνωση', 'κατανόηση', 'διόρθωση', 'συντήρηση'], 4);

    -- Κεφάλαιο 8 exercises
    INSERT INTO public.fill_blank_exercises (category_id, exercise_text, answers, difficulty_level) VALUES
    (cat8_id, 'Στη χρήση των εμφωλευμένων βρόχων: 1) ο ________ ________ πρέπει να βρίσκεται ________ μέσα στον ________. Ο βρόχος που ξεκινάει ________, πρέπει να ολοκληρώνεται ________.', ARRAY['εσωτερικός', 'βρόχος', 'ολόκληρος', 'εξωτερικό', 'τελευταίος', 'πρώτος'], 5),
    
    (cat8_id, '2) η είσοδος σε κάθε βρόχο υποχρεωτικά γίνεται από ___ ________ ___. 3) ______ ________ να χρησιμοποιηθεί η ίδια ____ __ ως ______ δύο ή περισσοτέρων ________ που ο ένας βρίσκεται στο ________ του άλλου.', ARRAY['την', 'αρχή', 'του', 'δεν', 'μπορεί', 'μεταβλητή', 'μετρητής', 'βρόχων', 'εσωτερικό'], 5),
    
    (cat8_id, 'Ο ________ παράγει ένα πρόγραμμα σε γλώσσα μηχανής που είναι τελείως ανεξάρτητο από το αρχικό πρόγραμμα. Ο ________ διαβάζει μία προς μία τις εντολές του αρχικού προγράμματος και για καθεμία εκτελεί αμέσως μια ισοδύναμη ακολουθία εντολών μηχανής.', ARRAY['μεταγλωττιστής', 'διερμηνευτής'], 5),
    
    (cat8_id, 'Το πρόγραμμα που παράγεται από το μεταγλωττιστή λέγεται ________ ________ /αντικείμενο πρόγραμμα. Το αρχικό πρόγραμμα λέγεται ________ πρόγραμμα. Το πρόγραμμα που επιτρέπει αυτή τη σύνδεση ονομάζεται ________ – ________.', ARRAY['αντικείμενο', 'πρόγραμμα', 'πηγαίο', 'συνδέτης', 'φορτωτής'], 5),
    
    (cat8_id, 'Πίνακας είναι ένα σύνολο ________ ________ τύπου, τα οποία αναφέρονται με ένα ________ ________. Κάθε ένα από τα αντικείμενα που απαρτίζουν τον πίνακα λέγεται ________ του ________. Η αναφορά σε ________ στοιχεία του πίνακα γίνεται με το ________ του ________ ακολουθούμενο από ένα ________.', ARRAY['αντικειμένων', 'ίδιου', 'κοινό', 'όνομα', 'στοιχείο', 'πίνακα', 'ατομικά', 'όνομα', 'πίνακα', 'δείκτη'], 5),
    
    (cat8_id, 'Τμηματικός προγραμματισμός ονομάζεται η ________ ________ και ________ των προγραμμάτων ως ένα ________ από ________ ________ προγραμμάτων.', ARRAY['τεχνική', 'σχεδίασης', 'ανάπτυξης', 'σύνολο', 'απλούστερα', 'τμήματα'], 5);
END $$;